if (HasString(game.pov.currentcommandpattern, "scope")) {
  if (game.pov.currentcommandpattern.scope = "inventory") {
    return (ScopeInventory())
  }
  if (game.pov.currentcommandpattern.scope = "notheld") {
    return (ScopeVisibleNotHeld())
  }
  if (game.pov.currentcommandpattern.scope = "all") {
    return (ScopeVisible())
  }
  obj = GetObject(game.pov.currentcommandpattern.scope)
  if (not obj = null) {
    return (GetAllChildObjects(obj))
  }
  if (HasObject(player.parent, game.pov.currentcommandpattern.scope)) {
    obj = GetAttribute(player.parent, game.pov.currentcommandpattern.scope)
    return (GetAllChildObjects(obj))
  }
