itemlist = NewStringList()
sections = 0
startpos = 1
for (i, 1, LengthOf(splittext)) {
  if (Mid(splittext, i, 1) = "{") {
      sections = sections + 1
    }
    if (Mid(splittext, i, 1) = "}") {
    sections = sections - 1
  }
  if (Mid(splittext, i, 1) = ":" and 0 >= sections) {
    item = Mid(splittext, startpos, i - startpos)
    list add (itemlist, item)
    startpos = i + 1
  }
}
item = Mid(splittext, startpos)
list add (itemlist, item)
return (itemlist)

